from django.forms import *from django.utils.translation import gettext_lazy as _from .models_FSJUser import FSJUserfrom .forms_student import *from .forms_adjudicator import *from .forms_coordinator import *from .forms_award import *class ProfileForm(forms.Form):       ccid = CharField(label = _("ccid"), max_length = 255)       first_name = CharField(label = _("First Name"), max_length = 255)       last_name = CharField(label = _("Last Name"), max_length = 255)       email = EmailField(label = _("Email"))       lang_pref = ChoiceField(label = _("Language Preference"), choices = FSJUser.LANG_CHOICES)              def __init__(self, *args, **kwargs):              super(ProfileForm, self).__init__(*args, **kwargs)                         self.fields['ccid'].disabled = True              self.fields['email'].disabled = True        class AwardFormGeneral(forms.Form):		award_name = CharField(label = _("Award Name"))		description = CharField(label = _("Description"), widget = Textarea)		value = IntegerField(label = _("Value"))		programs = CharField(label = _("Programs"), widget = Textarea)		OPTIONS = (			(1, "First"),			(2, "Second"),			(3, "Third"),			(4, "Fourth"),			(5, "Fifth"),			)		years_of_study = MultipleChoiceField(label = _("Years of Study"), widget=CheckboxSelectMultiple, choices=OPTIONS)		deadline = DateField(label = _("Deadline"), widget=DateInput())		TRUE_FALSE_CHOICES = (			(True, 'Yes'),			(False, 'No')			)		documents_needed = ChoiceField(label = _("Documents Needed"), widget = Select(), choices = TRUE_FALSE_CHOICES)		is_active = ChoiceField(label = _("Is Active"), widget = Select(), choices = TRUE_FALSE_CHOICES)