from django.db import modelsfrom django.utils.translation import gettext_lazy as _class FSJUser(models.Model):    FRENCH = 'fr'    ENGLISH = 'en'    LANG_CHOICES = (        (FRENCH, _('French')),        (ENGLISH, _('English')),    )       ccid = models.CharField(max_length = 255, unique = True, verbose_name = _("CCID"))    first_name = models.CharField(max_length = 255, verbose_name = _("First Name"))    last_name = models.CharField(max_length = 255, verbose_name = _("Last Name"))    email = models.EmailField(max_length = 254, verbose_name= _("Email"))    userid = models.IntegerField()    lang_pref = models.CharField(max_length = 2, blank = False, choices = LANG_CHOICES, default = FRENCH, verbose_name = _("Language Preference"))        def __str__(self):        return self.ccid        def user_class(self):        return None          def get_profile_initial_data(self):        return {"ccid" : self.ccid, "first_name" : self.first_name, "last_name" : self.last_name, "email" : self.email, "lang_pref" : self.lang_pref}        def get_profile_form(self, request=None):        # implement this in children classes        return None            def update_FSJUser_from_profile_form(self, form):        self.lang_pref = form.data['lang_pref']        self.first_name = form.data['first_name']        self.last_name = form.data['last_name']        self.save() 