from django.db import modelsfrom .models_FSJUser import FSJUserfrom .forms import ProfileFormfrom django.utils.translation import gettext_lazy as _from django import formsclass Student(FSJUser):    FIRST = 1    SECOND = 2    THIRD = 3    FOURTH = 4    FIFTH = 5    YEAR_CHOICES = (        (FIRST, _('First')),        (SECOND, _('Second')),        (THIRD, _('Third')),        (FOURTH, _('Fourth')),        (FIFTH, _('Fifth')),    )        program = models.CharField(max_length = 255, verbose_name = _("Program"))    year = models.IntegerField(blank = False, choices = YEAR_CHOICES, default = FIRST, verbose_name = _("Year of Study"))    def user_class(self):        return "Student"                def get_program(self):        return self.program        def student_id(self):        return self.userid        def get_profile_form(self, request=None):        if request and request.POST:            profile_form = StudentProfileForm(request.POST, initial = self.get_profile_initial_data())        else:            profile_form = StudentProfileForm(initial = self.get_profile_initial_data())        return profile_form        def get_profile_initial_data(self):        initial_data = super(Student, self).get_profile_initial_data()        initial_data['student_id'] = self.userid        initial_data['program'] = self.program        initial_data['year'] = self.year        return initial_data        def update_FSJUser_from_profile_form(self, form):        super(Student, self).update_FSJUser_from_profile_form(form)        self.program = form.data['program']        self.save()class StudentProfileForm(ProfileForm):    student_id = forms.IntegerField()    program = forms.CharField(label = _("Program"), max_length = 255)    year = forms.ChoiceField(label = _("Year of Study"), choices = Student.YEAR_CHOICES)        def __init__(self, *args, **kwargs):        super(StudentProfileForm, self).__init__(*args, **kwargs)                   self.fields['student_id'].disabled = True        self.fields['year'].disabled = True