from django.contrib.auth import views as auth_viewsfrom django.urls import pathfrom . import viewsurlpatterns = [    path('', views.redirect_to_home, name='redirect_to_home'),    path('login/', auth_views.LoginView.as_view(template_name="FSJ/login.html", redirect_authenticated_user=True), name='login'),    path('logout/', auth_views.LogoutView.as_view(), name='logout'),    path('home/', views.home, name='home'),    path('profile/', views.profile, name='profile'),]