from django.core.exceptions import PermissionDeniedfrom django.contrib.auth.decorators import login_required, user_passes_testfrom django.http import HttpResponsefrom django.template import loaderfrom .models import *from .utils import *def is_coordinator(usr):    user = get_FSJ_user(usr)    if not isinstance(user, Coordinator):        raise PermissionDenied    return True@login_required@user_passes_test(is_coordinator)def coordinator_home(request, FSJ_user):    context = get_standard_context(FSJ_user)       template = loader.get_template("FSJ/home.html")    return HttpResponse(template.render(context, request))